import React, { useState } from 'react';
import './App.css';
import fetchWeatherData from './WeatherService';

function App() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);

  const handleSearch = async () => {
    const data = await fetchWeatherData(city);
    setWeather(data);
  };

  useEffect(() => {
    fetchFIRMSData();
}, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>HeatWaveSafety</h1>
      </header>
      <div className="main">
        <input type="text" value={city} onChange={e => setCity(e.target.value)} placeholder="Enter city" />
        <button className="button" onClick={handleSearch}>Get Weather</button>
        {weather && (
          <div className="body">
            <p>City Name: {weather.name}</p>
            <p>Temperature: {weather.main.temp} °F</p>
            <p>Weather Description: {weather.weather[0].description}</p>
            <p>Humidity: {weather.main.humidity} %</p>
            <p>Low/Min Temp: {weather.main.temp_min} °F</p>
            <p>High/Max Temp: {weather.main.temp_max} °F</p>
            <p>Wind Speed/Gusts: {weather.wind.speed} mph</p>
          </div>
        )}
      </div>
    </div>
  );
}
//Not sure what are wind gusts vs. speed and not sure what '%' is for humidity
export default App;
