import axios from 'axios';

//const API_KEY = 'c1a2f74d2e7ee1d23d0dfe61900c4e61';
//const BASE_URL = 'https://api.openweathermap.org/data/2.5';

const fetchWeatherData = async (city) => {
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=c1a2f74d2e7ee1d23d0dfe61900c4e61&units=imperial`;
  try {
    const response = await axios.get(url);
    return response.data;
  } catch (error) {
    console.error('Error fetching weather data:', error);
  }
};

const fetchFIRMSData = async () => {
  const url = "https://firms.modaps.eosdis.nasa.gov/usfs/api/area/csv/e9b71104463f4639ddf682844d4b0dd1/VIIRS_SNPP_NRT/world/1/2025-01-27"; // Adjust this URL
  try {
      const response = await axios.get(url);
      console.log('FIRMS Data:', response.data);
  } catch (error) {
      console.error('Error fetching FIRMS data:', error);
  }
};
