import React, { useState } from 'react';
import './App.css';
import fetchWeatherData from './WeatherService';

function App() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);

  const handleSearch = async () => {
    const data = await fetchWeatherData(city);
    setWeather(data);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>HeatWaveSafety</h1>
      </header>
      <div className="main">
        <input type="text" value={city} onChange={e => setCity(e.target.value)} placeholder="Enter city" />
        <button onClick={handleSearch}>Get Weather</button>
        {weather && (
          <div>
            <p>City Name: {weather.name}</p>
            <p>Temperature: {weather.main.temp} 째F</p>
            <p>Weather: {weather.weather[0].description}</p>
            <p>Humidity: {weather.main.humidity} 째F</p>
            <p>Min Temp: {weather.main.temp_min} 째F</p>
            <p>Max Temp: {weather.main.temp_max} 째F</p>
            <p>Wind speed: {weather.wind.speed} m/h</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
